test.config:
indexer
{
	mem_limit		= 16M
}

searchd
{

	compat_results	= 0
}

source test1
{
	type			= mysql

	sql_query		= select id, gid, gid as a, title from test_table
	sql_attr_uint	= gid
	sql_attr_uint	= a
}

source test2 : test1
{
	sql_query		= select id+10, gid, gid as b, title from test_table
	sql_attr_uint	= gid
	sql_attr_uint	= b
}

index test1
{
	source			= test1
	path			= /test1
}

index test2
{
	source			= test2
	path			= /test2
}

index dist1
{
	type			= distributed
	local			= test1
	local			= test2
}

test.db_create:
create table test_table
(
	id int not null,
	gid int not null,
	title varchar(255) not null
);
